// üß≠ NAVBAR COMPONENT - Uses Zustand for auth state
// Demonstrates how components now access auth state directly from Zustand

import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
// ‚úÖ Import Zustand hooks from store
import { useAuthUser, useAuthStatus, useAuthActions } from '../store/authStore';

const Navbar = () => {
  const navigate = useNavigate();
  
  // üéØ ZUSTAND SELECTORS - Only subscribe to specific data (optimized performance)
  
  // üë§ Get user data - Component only re-renders when user changes
  const user = useAuthUser();
  console.log('üß≠ Navbar: Current user:', user);
  
  // üîí Get auth status - Component only re-renders when auth status changes  
  const { isAuthenticated } = useAuthStatus();
  console.log('üß≠ Navbar: Authentication status:', isAuthenticated);
  
  // üéÆ Get auth actions - Component never re-renders for this (actions don't change)
  const { logout } = useAuthActions();

  // üö™ Logout Handler - Calls centralized logout function
  const handleLogout = () => {
    console.log('üß≠ Navbar: User clicked logout');
    logout(); // This will:
    // 1. Remove token from localStorage and axios
    // 2. Clear user state in Zustand store
    // 3. Update isAuthenticated to false
    // 4. All components will automatically get the updates
    navigate('/'); // Redirect to home page
  };

  // ‚úÖ AUTHENTICATED USER MENU - When user is logged in
  const authLinks = (
    <>
      <li>
        <Link to="/dashboard" className="nav-link">
          Dashboard
        </Link>
      </li>
      <li>
        <Link to="/products" className="nav-link">
          Products
        </Link>
      </li>
      <li>
        {/* üëã Welcome Message - Shows current user name and role */}
        <span className="nav-link">
          Hello, {user?.name || 'User'}
          {user?.role && <small> ({user.role})</small>}
        </span>
      </li>
      <li>
        {/* üö™ Logout Button - Triggers centralized logout */}
        <button 
          onClick={handleLogout} 
          className="nav-link" 
          style={{ background: 'none', border: 'none', cursor: 'pointer' }}
        >
          Logout
        </button>
      </li>
    </>
  );

  // ‚ùå NON-AUTHENTICATED USER MENU - When user is not logged in
  const guestLinks = (
    <>
      <li>
        <Link to="/products" className="nav-link">
          Products
        </Link>
      </li>
      <li>
        <Link to="/register" className="nav-link">
          Register
        </Link>
      </li>
      <li>
        <Link to="/login" className="nav-link">
          Login
        </Link>
      </li>
    </>
  );

  return (
    <nav className="navbar">
      <div className="container">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%' }}>
          {/* üè† App Logo */}
          <Link to="/" className="navbar-brand">
            üå± Farm to Home
          </Link>
          
          {/* üîÄ CONDITIONAL NAVIGATION - Based on authentication status from Zustand */}
          <ul className="navbar-nav">
            {isAuthenticated ? authLinks : guestLinks}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
