// üîë LOGIN PAGE - Uses Zustand for authentication
// Handles user login and redirects to dashboard on success

import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
// ‚úÖ Import Zustand hooks instead of Context
import { useAuthStatus, useAuthActions } from '../context/AuthContext';

const Login = () => {
  // üìù LOCAL FORM STATE - Manages form input data
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [alert, setAlert] = useState(null);

  const { email, password } = formData;
  const navigate = useNavigate();

  // üéØ ZUSTAND SELECTORS - Get auth state and actions
  const { isAuthenticated, loading, error } = useAuthStatus();
  const { login, clearError } = useAuthActions();

  // üîÑ SIDE EFFECTS - Handle navigation and error display
  useEffect(() => {
    console.log('üîë Login: Auth state changed - authenticated:', isAuthenticated, 'error:', error);
    
    // ‚úÖ REDIRECT ON SUCCESS - If user is authenticated, go to dashboard
    if (isAuthenticated) {
      console.log('‚úÖ Login: User authenticated, redirecting to dashboard');
      navigate('/dashboard');
    }

    // ‚ùå SHOW ERRORS - Display any authentication errors
    if (error) {
      console.log('‚ùå Login: Showing error:', error);
      setAlert({ type: 'danger', message: error });
      clearError(); // Clear error from Zustand store
    }
  }, [error, isAuthenticated, navigate, clearError]);

  // üìù FORM HANDLERS
  const onChange = (e) => {
    console.log('üìù Login: Form field changed:', e.target.name);
    setFormData({ ...formData, [e.target.name]: e.target.value });
    // Clear local alert when user starts typing
    if (alert) setAlert(null);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    console.log('üîë Login: Form submitted for email:', email);
    
    // üîç CLIENT-SIDE VALIDATION
    if (email === '' || password === '') {
      console.log('‚ùå Login: Validation failed - empty fields');
      setAlert({ type: 'danger', message: 'Please fill in all fields' });
      return;
    }

    // üöÄ CALL ZUSTAND LOGIN ACTION
    console.log('üì§ Login: Calling Zustand login action');
    const result = await login({ email, password });
    
    // The login function returns { success: boolean, error?: string }
    // Success/error handling is done in useEffect above
    if (result.success) {
      console.log('üéâ Login: Login successful, Zustand will handle navigation');
    } else {
      console.log('‚ùå Login: Login failed, error will be shown by useEffect');
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={onSubmit} className="auth-form">
        <h2 className="auth-title">Login</h2>
        
        {alert && (
          <div className={`alert alert-${alert.type}`}>
            {alert.message}
          </div>
        )}

        <div className="form-group">
          <label htmlFor="email" className="form-label">Email Address</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={onChange}
            required
            className="form-input"
            placeholder="Enter your email"
          />
        </div>

        <div className="form-group">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={onChange}
            required
            className="form-input"
            placeholder="Enter your password"
          />
        </div>

        <button type="submit" className="btn btn-primary" style={{ width: '100%' }}>
          Login
        </button>

        <div className="auth-link">
          <p>
            Don't have an account? <Link to="/register">Sign up here</Link>
          </p>
        </div>
      </form>
    </div>
  );
};

export default Login;
